section.programlisting:
#include <stdio.h>
#include <sys/types.h>
#include <dirent.h>

int main ()
{
	DIR *d = opendir ( "." );
	struct dirent *e;
	if ( d = NULL ) return ( 1 );
	while ( e = readdir(d), e != NULL ) 
	{
		printf ( "%s %lu\n", e->d_name, e->d_ino );
	}
	closedir ( d );
	return ( 0 );
}

#include <stdio.h>
#include <sys/types.h>
#include <dirent.h>

int main ()
{
	DIR *d = opendir ( "." );
	struct dirent *e;
	if ( d == NULL ) return ( 1 );
	while ( e = readdir(d), e != NULL )
	{
		( void ) printf ( "%s %lu\n", e->d_name, ( unsigned long ) e->d_ino );
	}
	( void ) closedir ( d );
	return ( 0 );
}

